INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2017-07-05 23:10:34', '2017-07-06 02:10:34', 'Bem-vindo ao WordPress. Esse é o seu primeiro post. Edite-o ou exclua-o, e então comece a escrever!', 'Olá, mundo!', '', 'publish', 'open', 'open', '', 'ola-mundo', '', '', '2017-07-05 23:10:34', '2017-07-06 02:10:34', '', 0, 'http://127.0.0.1/lipe/?p=1', 0, 'post', '', 1),
(2, 1, '2017-07-05 23:10:34', '2017-07-06 02:10:34', 'Esta é uma página de exemplo. É diferente de um post porque ela ficará em um local e será exibida na navegação do seu site (na maioria dos temas). A maioria das pessoas começa com uma página de introdução aos potenciais visitantes do site. Ela pode ser assim:\n\n<blockquote>Olá! Eu sou um bike courrier de dia, ator amador à noite e este é meu blog. Eu moro em São Paulo, tenho um cachorro chamado Tonico e eu gosto de caipirinhas. (E de ser pego pela chuva.)</blockquote>\n\nou assim:\n\n<blockquote>A XYZ foi fundada em 1971 e desde então vem proporcionando produtos de qualidade a seus clientes. Localizada em Valinhos, XYZ emprega mais de 2.000 pessoas e faz várias contribuições para a comunidade local.</blockquote>\nComo um novo usuário do WordPress, você deve ir até o <a href=\"http://127.0.0.1/lipe/wp-admin/\">seu painel</a> para excluir essa página e criar novas páginas com seu próprio conteúdo. Divirta-se!', 'Página de Exemplo', '', 'publish', 'closed', 'open', '', 'pagina-exemplo', '', '', '2017-07-05 23:10:34', '2017-07-06 02:10:34', '', 0, 'http://127.0.0.1/lipe/?page_id=2', 0, 'page', '', 0),
(4, 1, '2017-07-10 10:21:12', '2017-07-10 13:21:12', '<img class=\"size-medium wp-image-8 alignleft\" src=\"http://127.0.0.1/lipe/wp-content/uploads/2017/07/Funday-Prize1-300x187.jpg\" alt=\"\" width=\"300\" height=\"187\" />O FUNDAY Free English Class aconteceu nos dias 21 e 22 de junio. Em sua segunda edição, o evento trouxe muita diversão, prêmios e aprendizagem em sua sede...', 'Funday Free English Class', '', 'publish', 'open', 'open', '', 'funday-free-english-class', '', '', '2017-07-11 15:22:50', '2017-07-11 18:22:50', '', 0, 'http://127.0.0.1/lipe/?p=4', 0, 'post', '', 0),
(6, 1, '2017-07-10 10:21:12', '2017-07-10 13:21:12', 'O FUNDAY Free English Class aconteceu nos dias 21 e 22 de junio. Em sua segunda edição, o evento trouxe muita diversão, prêmios e aprendizagem em sua sede...', 'Funday Free English Class', '', 'inherit', 'closed', 'closed', '', '4-revision-v1', '', '', '2017-07-10 10:21:12', '2017-07-10 13:21:12', '', 4, 'http://127.0.0.1/lipe/2017/07/10/4-revision-v1/', 0, 'revision', '', 0),
